insert  into `v9_dest_province`(`id`,`proName`,`province`,`title`,`description`,`banner`,`imgthunb`,`dateline`,`iphoneapp`,`androidapp`,`recommend`,`close`,`recommends`) values (1,'安徽','anhui','旅游难忘安徽','<span>历史：</span>清康熙6年设安徽省，以政治中心安庆府（今安庆）和经济中心徽州府（今歙县）各取首字得名。因境内有皖山（天柱山），故简称”皖“，一说安徽为春秋时皖国故地，故简称”皖“。<br/><span>名人：</span>曹操，朱元璋，直隶总督李鸿章，台湾巡抚刘铭传，革命家陈独秀，政治家吴邦国，李克强，苏宁电器张近东，雨润食品祝义才，比亚迪王传福，巨人史玉柱，主持人周涛，演员李小璐。<br/><span>特色：</span>巢县干丝，乌饭团，小笼渣肉蒸饭，老鸭汤，苎叶保，一闻香包子，蒋大顺米查肉，鸡丝辣汤，鳝鱼辣汤，小茶糕，罐子汤，酥鸭面，包河藕粥，虾子面，鱼皮蟹黄饺，江万春水饺，正福斋汤团，混汤酒酿元宵，烫鸭肠，五香狗肉。 ','dest/anhui.jpg','dest/anhui_320.jpg',1354589857,'app1','app2',1,1,1),(2,'澳门','aomen','感受澳门 动容时刻','<span>历史：</span>明为船只停航寄泊的地方，因隶属广东香山，又称“香山澳”，因本地内港的妈祖庙，隔海同湾仔的银坑相望，形成的海峡象门，故曰澳门，取全称中的“澳”字作为简称。<br/><span>名人：</span>赌王何鸿燊，演员容韵琳，李菲，李嘉欣，方中信，何超仪，甄妮，黄伟麟，古卓文，陈冠希<br/><span>特色：</span>礼记杏仁饼、蛋卷、凤凰卷、牛肉干、猪油糕、蛋卷仔、光酥饼、姜糖、花生糖、甜老婆饼，咸切酥，牛耳仔，咸老公饼，鸡仔饼，合桃酥，姜汁肉松米通、芝麻饼 ','dest/aomen.jpg','dest/aomen_320.jpg',1354589857,'app1','app2',1,1,1),(3,'北京','beijing','东方古都 长城故乡','<span>历史：</span>北京古称“蓟”，是战国时期燕国都城，明成祖朱棣从南京迁都于此，改称“北京”，取全称中的“京”字作为简称。<br/><span>名人：</span>元代天文学家郭守敬，元代剧作家马致远，清代小说家曹雪芹，京剧表演艺术家梅兰芳，近代作家老舍，相声表演艺术家侯宝林，著名导演冯小刚，陈凯歌，演员葛优，姜文，章子怡。<br/><span>特色：</span>北京烤鸭，豆汁，卤煮火烧，炒肝儿，爆肚，艾窝窝，驴打滚，褡裢火烧，炒疙瘩，灌肠，糖耳朵。 ','dest/beijing.jpg','dest/beijing_320.jpg',1354589857,'app1','app2',1,1,1),(4,'福建','fujian','山海画廊 人间福地','<span>历史：</span>秦设闽中郡，唐设福建节度使，管辖福州、建州、泉州、漳州、汀州，明初设福建省，以福州、建州各取首字得名。辖区古为闽越族聚居地，故简称“闽”，一说因境内有闽江，故简称“闽”。<br/><span>名人：</span>北宋宰相蔡京，明末大将洪承畴，民族英雄郑成功，清政治家林则徐，教育家严复，文学家冰心，数学家陈景润,演员姚晨，运动员林丹，新华都陈发树<br/><span>特色：</span>甜凉糕，汀州豆腐干，手抓面，米冻皮，土笋冻，麻糍，酥糍丸，醉蚶瓣，烧卖，糯米丸，肉丸，叉烧肉粽，客家八宝粽，糯米甜馃，九重馃，萝卜丝菠菠馃， ','dest/fujian.jpg','dest/fujian_320.jpg',1354589857,'app1','app2',1,1,1),(5,'甘肃','gansu','行丝绸之路唐蕃古道<br>赏陇上江南文化瑰宝','<span>历史：</span>甘肃之称始于宋代，以甘州（今张掖）、肃州（今酒泉）各取首字得名，取全称中的“甘”字作为简称。一说认为西夏时行政中心在甘州，故简称“甘”，甘肃古时别称陇西，故又简称“陇”。<br/><span>名人：</span>飞将军李广，思想家王符，三国名将姜维，铁人王进喜，主持人朱军，李修平，水均益，演员罗海琼，歌手魏晨，soho潘石屹<br/><span>特色：</span>兰州拉面，黑瓜子，浆水面，河州大饼，羊肉泡馍，千层牛肉饼。 ','dest/gansu.jpg','dest/gansu_320.jpg',1354589857,